function toggleText(t){t.classList.contains("hide")?(t.classList.remove("hide"),t.classList.add("show")):t.classList.contains("show")&&(t.classList.remove("show"),t.classList.add("hide"))}function activeButton(t){this.buttons=document.getElementById("btn-filters");for(let t=0;t<this.buttons.childElementCount;t++)this.buttons.children[t].classList.remove("active");t.classList.add("active")}function filterStore(t){if(this.store=document.getElementById("store-items"),"all"===t)for(let t=0;t<this.store.childElementCount;t++)this.store.children[t].classList.remove("hide"),this.store.children[t].classList.add("show");else for(let e=0;e<this.store.childElementCount;e++)this.store.children[e].dataset.item===t?(this.store.children[e].classList.remove("hide"),this.store.children[e].classList.add("show")):(this.store.children[e].classList.remove("show"),this.store.children[e].classList.add("hide"))}function eventListeners(){document.querySelectorAll(".article").forEach(t=>{t.addEventListener("click",t=>{let e=null;t.target.classList.contains("article")?e=t.target:t.target.parentElement.classList.contains("article")?e=t.target.parentElement:t.target.parentElement.parentElement.classList.contains("article")&&(e=t.target.parentElement.parentElement),toggleText(e.children[2])})}),document.querySelectorAll(".filter-btn").forEach(t=>{t.addEventListener("click",t=>{activeButton(t.target),filterStore(t.target.dataset.filter)})})}document.addEventListener("DOMContentLoaded",()=>{eventListeners()});