const body=document.getElementById("body"),logo=document.getElementById("optica-logo"),hamburger=document.getElementById("hamburger"),navLinks=document.getElementById("nav-links"),navItems=document.querySelectorAll("#nav-links li"),links=document.getElementsByClassName("link");function navClicked(){body.classList.toggle("no-scroll"),navLinks.classList.toggle("open"),navItems.forEach(e=>e.classList.toggle("fade")),hamburger.classList.toggle("menu-open");for(const e of hamburger.children)e.classList.toggle("menu-open")}logo.addEventListener("click",()=>{navLinks.classList.contains("open")&&navClicked(),body.classList.remove("no-scroll")}),hamburger.addEventListener("click",()=>navClicked());for(const e of links)e.addEventListener("click",()=>{navClicked(),body.classList.remove("no-scroll")});